mapper.insert:
/* Reply - replyInsert */

	select spring_reply_seq.nextval from dual

insert into spring_reply(
r_num, b_num, r_name, r_content, r_pwd
)values(
#{r_num}, #{b_num}, #{r_name}, #{r_content}, #{r_pwd}
)

mapper.select:
/* Reply - pwdConfirm */
SELECT NVL((
SELECT 1 FROM spring_reply
WHERE r_num = #{r_num} AND r_pwd = #{r_pwd}
), 0) as state
FROM dual

mapper.update:
/* Reply - replyUpdate */
update spring_reply
set r_content = #{r_content},
r_date = sysdate
where r_num = #{r_num}
